const rotateBottomLeft="data:image/png;base64,";
const rotateBottomRight="data:image/png;base64,";
const rotateTopRight="data:image/png;base64,";
const rotateTopLeft="data:image/png;base64,";
const leftRight="data:image/png;base64,";
const topBottom="data:image/png;base64,";
const topLeft="data:image/png;base64,";
const topRight="data:image/png;base64,";


export enum Direction {
	top = 'top',
	right = 'right',
	bottom = 'bottom',
	left = 'left',
	topRight = 'topRight',
	bottomRight = 'bottomRight',
	bottomLeft = 'bottomLeft',
	topLeft = 'topLeft',
}


const getSvg=(icon:string,transform:string)=>`data:image/svg+xml,${encodeURI(`<svg version="1.1" id="Layer_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" 
x="0px" y="0px" width="64px" height="64px" viewBox="0 0 64 64" 
enable-background="new 0 0 64 64" style="transform: ${transform};" xml:space="preserve">  
    <image id="image0" width="64" height="64" x="0" y="0"
           href="${icon}" ></image>
</svg>`)}`;

export function getMouseIcon(direction:Direction,transform:string){
	let sizeSvg=leftRight;

	let rotateSvg=rotateTopLeft;
	switch (direction){
		case Direction.topLeft:
			sizeSvg=topLeft;
			break;
		case Direction.topRight:
			rotateSvg=rotateTopRight;
			sizeSvg=topRight;
			break;
		case Direction.bottomLeft:
			rotateSvg=rotateBottomLeft;
			sizeSvg=topRight;
			break;
		case Direction.bottomRight:
			sizeSvg=topLeft;
			rotateSvg=rotateBottomRight;
			break;
		case Direction.top:
		case Direction.bottom:
			sizeSvg=topBottom;
			break;
		case Direction.left:
		case Direction.right:
			sizeSvg=leftRight;
			break;
	}
	return{sizeSvg:`url("${getSvg(sizeSvg,transform)}")`,rotateSvg:`url("${getSvg(rotateSvg,transform)}")`};
}

export const setCursor=(svg:string)=>`cursor:${svg} 15 15,auto ;
    cursor: -webkit-image-set(${svg} 2x,${svg} 1x)15 15,auto !important;`;
